mapper.select:
select R1.*
	   from
		     (select *
		    from hire
		    where id=#{id}
		    order by hire_id asc
		    )R1
	    limit #{limit} offset #{offset}

mapper.update:

update hire
 set hire_open =#{hire_open}
 where hire_id =#{hire_id}

mapper.select:
select count(*)
from hire
where id=#{id}
