mapper.select:
select R1.*
   from
	     (select *
	    from resume
	    where id=#{id}
	    order by resume_id asc
	    )R1
    limit #{limit} offset #{offset}

mapper.update:
update resume
set resume_state =#{resume_state}
where resume_id =#{resume_id}

mapper.select:
select count(*)
from resume
where id=#{id}

 SELECT count(*)
FROM message
WHERE message_receiver =#{id}

mapper.update:
